 function getCookie(c_name)
 {
 var i,x,y,ARRcookies=document.cookie.split(";");
 for (i=0;i<ARRcookies.length;i++)
   {
   x=ARRcookies[i].substr(0,ARRcookies[i].indexOf("="));
   y=ARRcookies[i].substr(ARRcookies[i].indexOf("=")+1);
   x=x.replace(/^\s+|\s+$/g,"");
   if (x==c_name)
  {
  return unescape(y);
  }
   }
 }

 function setCookie(c_name,value,exdays)
 {
 var exdate=new Date();
 exdate.setDate(exdate.getDate() + exdays);
 var c_value=escape(value) + ((exdays==null) ? "" : "; expires="+exdate.toUTCString());
 document.cookie=c_name + "=" + c_value;
 }

 function calljs(cid)
 {
  if(cid.checked)
  {
    setCookie("act_username",document.getElementById("act_username").value, 365);
    
    setCookie("act_password",document.getElementById("act_password").value, 365);
  }
  else
  {
    setCookie("act_username","", -1);
    
    setCookie("act_password","", -1);
  }
 }
 
 function checkCookie()
 {
  var act_username=getCookie("act_username");
  
  var act_password = getCookie("act_password");
  
   if (act_username!=null && act_username!="")
    {
     document.getElementById("act_username").value = act_username;
    }
    
   if (act_password!=null && act_password!="")
    {
     document.getElementById("act_password").value = act_password ;
    }
    
    if( act_username!=null && act_username!="" && act_password!=null && act_password!="" )
     {
       document.getElementById("remember").checked = true ;
     }
     else
     {
     
     }
     
 }
 checkCookie();
